actions.script:
// get inventory item type
localVars.inventoryItemType = Object.keys(inventoryItemTypes)[localVars.inventoryItemTypeIndex]
// get miner box instance
const minerBoxes = runtime.objects.Miner_Box.getAllInstances()
for (var i = 0; i < minerBoxes.length; i++) {
  var minerBox = minerBoxes[i]
  if (minerBox.instVars["PEERID"] == runtime.globalVars.myPeerId) {
  	// get number of items of item type in minerbox
	localVars.numItemsOfItemTypeInMinerBox = minerBox.instVars[localVars.inventoryItemType]
  }
}

const minerBoxes = runtime.objects.Miner_Box.getAllInstances()
for (var i = 0; i < minerBoxes.length; i++) {
  var minerBox = minerBoxes[i]
  if (minerBox.instVars["PEERID"] == runtime.globalVars.myPeerId) {
  	minerBox.instVars[localVars.ItemType] = 0
  }
}

const minerBoxes = runtime.objects.Miner_Box.getAllInstances()
for (var i = 0; i < minerBoxes.length; i++) {
  var minerBox = minerBoxes[i]
  if (minerBox.instVars["PEERID"] == runtime.globalVars.myPeerId) {
  	minerBox.instVars[localVars.ItemType] -= 1
  }
}

const minerBoxes = runtime.objects.Miner_Box.getAllInstances()
for (var i = 0; i < minerBoxes.length; i++) {
  var minerBox = minerBoxes[i]
  if (minerBox.instVars["PEERID"] == localVars.peerId) {
  	minerBox.instVars[localVars.ItemType] = 0
  }
}

const minerBoxes = runtime.objects.Miner_Box.getAllInstances()
for (var i = 0; i < minerBoxes.length; i++) {
  var minerBox = minerBoxes[i]
  if (minerBox.instVars["PEERID"] == localVars.peerId) {
  	minerBox.instVars[localVars.ItemType] -= 1
  }
}

const minerBoxes = runtime.objects.Miner_Box.getAllInstances()
for (var i = 0; i < minerBoxes.length; i++) {
  var minerBox = minerBoxes[i]
  minerBox.instVars["weight"] = 0 
  minerBox.instVars["value"] = 0
  for (var j = 0; j < Object.keys(inventoryItemTypes).length; j++) {
  	var itemType = Object.keys(inventoryItemTypes)[j]
	minerBox.instVars["weight"] += minerBox.instVars[itemType] * inventoryItemTypes[itemType]["weight"]
	minerBox.instVars["value"] += minerBox.instVars[itemType] * inventoryItemTypes[itemType]["value"]
  }
}
